<template>
    <div class="map_section_main flex-row">

        <div class="tooltip_container flex-row">
            <div class="title_block flex-column">
                <span>{{ this.current_chp.name }}</span>
                <div class="chevron_left"></div>
            </div>

            <div class="content_block">
                <img src="@/assets/images/map_tooltip_example.jpg" alt="">
                <div class="text_block flex-column">
                    <div class="title_row flex-row">
                        <span class="key">Название:</span>
                        <span class="value">{{ this.current_chp.name }}</span>
                    </div>
                    <p class="value">
                        {{ this.current_chp.desc }}
                    </p>
                    <div class="region_row flex-row">
                        <span class="key">Велаят:</span>
                        <span class="value">{{ this.current_chp.region }}</span>
                    </div>
                    <div class="border_row flex-row">
                        <span class="key">Граница:</span>
                        <span class="value">{{ this.current_chp.bordering }}</span>
                    </div>                
                    <div class="contacts_row flex-row">
                        <span class="key">Контакты:</span>
                        <div class="values_block flex-column">
                            <span class="value" v-for="(element, index) in this.current_chp.contacts" :key="index">{{ element }}</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>

        <div class="inner_wrapper flex-row">
            <span class="background_text">{{ $t('map_section.background_text') }}</span>
            
