        showToolTip(event){
            const tooltip = document.querySelector('.tooltip_container')
            tooltip.style.display = "flex"
            tooltip.style.left = event.clientX + 5 + "px";
            tooltip.style.top = (event.clientY - tooltip.getBoundingClientRect().height / 2) - 20 + "px";
            let checkpoint_hovered = event.target.id
            this.current_chp = this.checkpoints[checkpoint_hovered]


        },
        hideToolTip(event){
            const tooltip = document.querySelector('.tooltip_container')
            tooltip.style.display = "none"
        }
